<script lang="ts">
	// This component is horrible
	import Vue from 'vue'
	import { TimelineMax } from 'gsap';
	import LanguagePicker from "./LanguagePicker.vue";

	export default Vue.extend({
		components: {LanguagePicker},
		name: "Navbar",
		data() {
			let isOpen: boolean = false;
			let isLanguagePickerOpen: boolean = false;
			let tl = new TimelineMax();
			return {
				isOpen,
				isLanguagePickerOpen,
				tl
			}
		},
		methods: {
			openLanguagePicker() {
				// Spaghetti Zone, I'm sorry
				this.tl.set('#language-pick', {
					x: 1900,
				})

				this.tl.to("#language-pick", {
					x: 0,
					duration: 1
				});
				this.tl.progress(1).progress(0);

				let cl = false;
				if(cl === false) {
					this.tl.play();
					cl = true;
				} else {
					this.tl.reverse();
					cl = false;
				}
				this.isLanguagePickerOpen = true;
			},
			closeLanguagePicker() {
				// I dunno why but there is a stupid delay before the reverse
				this.tl.reverse();
				this.tl.eventCallback("onReverseComplete", () => {
					this.isLanguagePickerOpen = false;
				});
			}
		}
	})
</script>

